//====================[ 定義 ]====================

const IS_DEBUG_CONNECT_LOGGING = true;	// (debug) 通信ログを出力するか
const CONNECT_INTERVAL_MS = 1000;	// 通信ごとの待機時間（ミリ秒）

const HOST = location.hostname;
const SERVER_NAME = (() => {
	const m = HOST.match(/^(.*)\.3gokushi/);
	if (m && m.length >= 2) {
		return m[1];
	}
	return 'local';
})();
const SAVENAME = SERVER_NAME + "_bro3_fortinfo_tool_ex_20180630_"; //グリモン領域への保存時のPGの名前;

// 状態表示用定数
const FORTINFO_STATE_NEUTRAL = 0;	// ニュートラル
const FORTINFO_STATE_CHECK = 1; 	// 検出中
const FORTINFO_STATE_DETECT = 2;	// 検出完了
const FORTINFO_STATE_WORK = 3;		// 調査処理中
const FORTINFO_STATE_ABORTING = 4;	// 中断処理中

// 内部データ用変数
let working = false;		// 調査処理中か
let aborting = false;		// 中断処理中か

//====================[ 設定 ]====================

let g_options = {};
const MODE_01 = 'fimo01';	// 詳細モード
const MODE_02 = 'fimo02';	// シンプルモード
const SEASON_01 = 'fise01'; // 1期
const SEASON_02 = 'fise02'; // 2期
const SEASON_03 = 'fise03'; // 3-4期
const SEASON_04 = 'fise04'; // 5-9期/5-6期
const SEASON_05 = 'fise05'; // 10-13期/7-12期
const SEASON_06 = 'fise06'; // 14期-/13期-
const AREA_01 = 'fiar01';	// 北西エリア
const AREA_02 = 'fiar02';	// 北東エリア
const AREA_03 = 'fiar03';	// 南西エリア
const AREA_04 = 'fiar04';	// 南東エリア
const AREA_05 = 'fiar05';	// 武将砦/城エリア
const AXIS_01 = 'fiax01';	// 座標リスト

// デフォルトオプション定義の取得
const defaultOptions = (() => {
	let options = {};
	options[MODE_01] = 1;	// 詳細
	options[SEASON_01] = 1; // 1期
	options[AREA_01] = 1;	// 方面
	options[AXIS_01] = '';	// 座標
	return options;
})();

// 設定の保存 (nothrows)
async function saveSettingsWithObject(obj) {
	await cs_setValue(SAVENAME + '_options', JSON.stringify(obj));
}

// 設定のロード (nothrows)
async function loadSettings() {
	// 保存データの取得
	const obj = await cs_getValue(SAVENAME + '_options', null);
	if (obj === null) {
		g_options = defaultOptions;
		return;
	}

	let options = JSON.parse(obj);

	// 保存データにデフォルト設定の情報がない場合、デフォルト設定を追加
	for (const key in defaultOptions) {
		if (typeof options[key] === "undefined") {
			options[key] = defaultOptions[key];
		}
	}

	g_options = options;
}

//====================[ メイン ]====================

const main = async () => {
	await loadSettings();
	createMainWindow();
}; main();

//====================[ 画面 ]====================

