// 同盟名取得 (nothrows)
function getAllyName() {
	return new Promise(async (resolve) => {
		let html;
		try {
			html = await connect(`http://${HOST}/user/`);
		}
		catch (error) {
			console.log(error);
			resolve(null);
			return;
		}
		let name = html.match(/<td[^>]*>同盟<\/td>[^<]*<td[^>]*>[^>]*><a href="[^"]*">([^<]+)<\/a>[^>]*<\/td>/);
		if (!name) {
			name = html.match(/<td[^>]*>同盟<\/td>[^<]*<td[^>]*>[^>]*<a href="[^"]*">([^<]+)<\/a>[^>]*<\/td>/);
		}
		let ally_name = (name && name.length >= 2) ? name[1] : null;

		// イベント鯖対応
		if (!ally_name) {
			const resp = $("<div>").append(html);
			const ally = $("table.commonTables td:contains(同盟)", resp).next();
			const name = $.trim($("a", ally).text());
			if (name.length) {
				ally_name = name;
			}
		}
		//

		resolve(ally_name);
	});
}

// 勢力情報あり（イベント鯖）
function genMapInfoInfluence(x,y,m,influence,h,k,g,l,e,c,b,f,j,d,i,a,p,t){
	info.influence = influence;	// 勢力(int)
	this.x = x; // center-x
	this.y = y; // center-y
	this.m = m; // 削除中
	this.h = h; // 地名
	this.k = k; // 君主名
	this.g = g; // 人口-
	this.l = l; // 座標(x,y)
	this.e = e; // 同盟名
	this.c = c.length; // 戦力★
	this.b = b; // 距離
	this.f = f; // 森
	this.j = j; // 岩
	this.d = d; // 鉄
	this.i = i; // 糧
	this.a = parseInt(a,10); // NPCフラグ
	this.p = p; // 保護期間
	this.t = t; // 深夜停戦
	this.xy = l.replace(/^\((-?[0-9]+\,-?[0-9]+)\)$/, "$1");
}

// MAP データをオブジェクトに変換して返す
function genMapInfo(x, y, m, h, k, g, l, e, c, b, f, j, d, i, a, p, t) {
	this.x = x; // (int) center-x
	this.y = y; // (int) center-y
	this.m = m; // 削除中
	this.h = h; // 地名
	this.k = k; // 君主名
	this.g = g; // 人口-
	this.l = l; // 座標(x,y)
	this.e = e; // 同盟名
	this.c = c.length; // (int) 戦力★
	this.b = b; // 距離
	this.f = f; // 森
	this.j = j; // 岩
	this.d = d; // 鉄
	this.i = i; // 糧
	this.a = toInt(a); // (int) NPCフラグ
	this.p = p; // 保護期間
	this.t = t; // (bool) 深夜停戦
	this.xy = l.replace(/^\((-?[0-9]+\,-?[0-9]+)\)$/, "$1");
}

// MAP データを取得 (nothrows)
function getMap(ally, x, y) {
	return new Promise(async (resolve) => {
		if (!ally) {
			ally = "";
		}

		let html;
		try {
			html = await connect(`http://${HOST}/map.php`, { x: x, y: y, type: 1 });
		}
		catch (error) {
			console.log(error);
			resolve([]);
			return;
		}

		//ターゲット座標を中心とした9マスのみを抜く
		const targets = [
			[x - 1, y - 1], [x - 1, y], [x - 1, y + 1],
			[x, y - 1], [x, y], [x, y + 1],
			[x + 1, y - 1], [x + 1, y], [x + 1, y + 1]
		];

		let neighbors = []; // 隣接同盟名
		let capture = '';	// 攻略済か
		let npc_name, npc_power;

		$("#mapOverlayMap > area[onmouseover]", html).each((index, area) => {
			let mapInfo; // eval は内部スコープで実行されるため、値を取り出すには上位層で宣言した変数に代入する必要がある
			const code = $(area).attr("onmouseover")
				.replace(/^.*rewriteAddRemoving/, "genMapInfo")
				.replace(/^.*rewriteInfluenceAddRemoving/, "genMapInfoInfluence")
				.replace(/\); .*$/, ");")
				.replace(/^genMapInfo\(/, `mapInfo = new genMapInfo(${x},${y},`)
				.replace(/^genMapInfoInfluence\(/, `mapInfo = new genMapInfoInfluence(${x},${y},`);
			eval(code);

			for (const target of targets) {
				const targetXY = `${target[0]},${target[1]}`;
				if (mapInfo.xy !== targetXY) {
					continue;
				}

				//ターゲット座標
				if (mapInfo.a === 1) {
					// NPC砦
					npc_name = mapInfo.h;
					if (npc_name.match(/^([南北][東西]砦)(\d{1,3})$/)) {
						// 北西砦1 -> 北西砦001 のように桁数を合わせる
						npc_name = RegExp.$1 + ("00" + RegExp.$2).slice(-3);
					}
					npc_power = mapInfo.c;
					if (mapInfo.e !== '-') {
						// 制圧同盟名を覚える
						capture = mapInfo.e;
					}
				} else {
					// 領地
					if (mapInfo.e.length) {
						// 隣接同盟を覚える
						neighbors.push(mapInfo.e);
					}
				}
				break;
			}
		});

		// 隣接空きマス数 [0...8]
		const vacant_cell_count = 8 - neighbors.length;

		// 隣接同盟の重複を外す
		neighbors = array_unique(neighbors);

		// 隣接状況
		const status = statusForNeighbors(ally, capture, neighbors, vacant_cell_count);

		// 競合同盟
		const conflicts = neighbors.filter(x => { return x != ally; });

		// 検出情報
		const info = [];
		info.push(npc_name);  // 領地名
		info.push(x);
		info.push(y);
		info.push(npc_power); // ★の数
		info.push(status);	  // 隣接状況
		info.push(conflicts.join(",")); // 競合同盟

		resolve(info);
	});
}

// 隣接状況
function statusForNeighbors(ally, capture, neighbors, vacant_cell_count) {
	let status = "";
	if (ally && ally.length) {
		// 指定同盟名の主観的な判定
		if (capture.length) {
			// 制圧情報
			if (capture === ally) {
				status = "制圧";
			} else {
				status = "負け";
			}
		} else if ($.inArray(ally, neighbors) === -1) {
			// 自同盟隣接なし
			if (vacant_cell_count) {
				status = `隣接空き${vacant_cell_count}マス`;
			} else {
				status = "隣接なし";
			}
		} else {
			// 自同盟隣接あり
			if (neighbors.length === 1) {
				if (vacant_cell_count) {
					status = `隣接(残${vacant_cell_count}マス)`;
				} else {
					status = "完全包囲";
				}
			}
			else if (neighbors.length >= 2) {
				status = `競合(空${vacant_cell_count}マス)`;
			}
		}
	} else {
		// 客観的な判定
		if (capture.length) {
			// 制圧情報
			status = `攻略済(${capture})`;
		} else {
			if (neighbors.length === 1) {
				status = "未攻略(完全包囲)";
			} else {
				status = `未攻略(空${vacant_cell_count}マス)`;
			}
		}
	}

	return status;
}

