// 同期タイムアウト処理
function timeout(ms) {
	return new Promise(resolve => setTimeout(resolve, ms));
}

// 通信ラッパー (nothrows)
function connect(url, params = null) {
	if (IS_DEBUG_CONNECT_LOGGING) {
		let more = "";
		if (params) {
			more = Object.keys(params).map(key => {
				const value = params[key];
				return `${key}=${value}`;
			}).join("&");
			if (more.length) {
				more = `?${more}`;
			}
		}
		timeLog(`connect> ${url}${more}`);
	}

	return new Promise(async (resolve, reject) => {
		try {
			const data = await $.get(url, params);
			await timeout(CONNECT_INTERVAL_MS);
			resolve(data);
		}
		catch (error) {
			reject(error);
		}
	});
}

// 設定を読む (nothrows)
function cs_getValue(key, defaultValue = null) {
	return new Promise(async (resolve) => {
		if (typeof GM_getValue !== 'undefined') {
			// legacy greasemonkey
			resolve(GM_getValue(key, defaultValue));
			return;
		}
		if (typeof GM !== 'undefined' && GM && typeof GM.getValue !== 'undefined') {
			// latest greasemonkey
			try {
				const value = await GM.getValue(key, defaultValue);
				resolve(value);
			}
			catch (error) {
				console.log(error);
				resolve(null);
			}
			return;
		}
		// localStorage
		let closure = () => {
			let value = sessionStorage.getItem(key);
			if (!value) {
				value = localStorage.getItem(key);
				if (!value) {
					return defaultValue;
				}
			}
			const type = value[0];
			value = value.substring(1);
			switch (type) {
				case 'b':
					return value == 'true';
				case 'n':
					return Number(value);
				default:
					return value;
			}
		};
		resolve(closure());
	});
}

// 設定を書く (nothrows)
function cs_setValue(key, value) {
	return new Promise(async (resolve) => {
		if (typeof GM_setValue !== 'undefined') {
			// legacy greasemonkey
			GM_setValue(key, value);
			resolve();
			return;
		}
		if (typeof GM !== 'undefined' && GM && typeof GM.setValue !== 'undefined') {
			// latest greasemonkey
			try {
				await GM.setValue(key, value);
				resolve();
			}
			catch (error) {
				console.log(error);
				resolve();
			}
			return;
		}
		// localStorage
		let closure = () => {
			value = (typeof value)[0] + value;
			try {
				localStorage.setItem(key, value);
				resolve();
			} catch (error) {
				console.log(error);
				localStorage.removeItem(key);
				sessionStorage.setItem(key, value);
				resolve();
			}
		};
		resolve(closure());
	});
}

// 設定を削除 (nothrows)
function cs_deleteValue(key) {
	return new Promise(async (resolve) => {
		if (typeof GM_deleteValue !== 'undefined') {
			// legacy greasemonkey
			GM_deleteValue(key);
			resolve();
			return;
		}
		if (typeof GM !== 'undefined' && GM && typeof GM.deleteValue !== 'undefined') {
			// latest greasemonkey
			try {
				await GM.deleteValue(key);
				resolve();
			}
			catch (error) {
				console.log(error);
				resolve();
			}
			return;
		}
		// localStorage
		sessionStorage.removeItem(key);
		localStorage.removeItem(key);
		resolve();
	});
}

