// 時刻付きロガー
function timeLog(text) {
	const now = new Date;
	const hh = ('00' + now.getHours()).slice(-2);
	const mm = ('00' + now.getMinutes()).slice(-2);
	const ss = ('00' + now.getSeconds()).slice(-2);
	const ms = ('000' + now.getMilliseconds()).slice(-3);
	const time = `${hh}:${mm}:${ss}.${ms}`;
	const code = `[${time}] ${text}`;
	console.log(code);

	// UI のロギングスペースにも反映
	$("#fortinfo_log").text(code);
}

// オブジェクトを10進数の数値型へ変換する。カンマ桁区切りの数字も対応。null または変換できなかったときは 0 を返す。
function toInt(obj) {
	return toNumber(obj, 0);
}

// オブジェクトを10進数の数値型へ変換する。カンマ桁区切りの数字も対応。null または変換できなかったときは defaultValue を返す。
function toNumber(obj, defaultValue = null) {
	if (obj === null) {
		return defaultValue;
	}
	const text = ("" + obj).replace(/,/g, '');
	const num = parseInt(text, 10);
	if (isNaN(num)) {
		return defaultValue;
	}
	return num;
}

// 配列をマージ
function array_merge(dest, src) {
	Array.prototype.push.apply(dest, src);
}

// 配列をユニーク化
function array_unique(array) {
	return array.filter((element, index) => {
		return array.indexOf(element) === index; // Chrome 系では、filter で第3引数がない
	});
}

// for debug print object
function po(obj, ext = "") {
	console.log(ext + JSON.stringify(obj, null, '\t'));
}
